(479, 'en', 'theme option-tree', 'b28671c8587dee7dddab1d280417f6bb', 'Edit Layout Data', 0),
(480, 'en', 'theme option-tree', '6c111e1acd49eb6d378aaae14fac6751', 'Go to the <code>Appearance->Theme Options</code> page.', 0),
(481, 'en', 'theme option-tree', 'be190433acc045eeea3ed980ff637531', 'Modify and save your theme options and the layout will be updated automatically.', 0),
(482, 'en', 'theme option-tree', '7128fc018254013464cf957940192347', 'Saving theme options data will update the currently active layout, so before you start saving make sure you want to modify the current layout.', 0),
(483, 'en', 'theme option-tree', 'cf83be315615dbeb612c03db572fac14', 'If you want to edit a new layout, first create it then save your theme options.', 0),
